import React, { useState } from 'react';
import axios from 'axios'
export const Login = (props) => {
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [emailError, setEmailError] = useState('')
    const [passError, setPassError] = useState('')
    const [errExist, setErrExist] = useState(0)

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {email, pass}

        // Email validation
        const emailRegex = /^\S+@\S+\.\S+$/;
        if (!emailRegex.test(email)) {
        setErrExist(1);
        setEmailError('Please enter a valid email address.');
        return;
        }

        // Password validation
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$/;
        if (!passwordRegex.test(pass)) {
        setErrExist(1);
        setPassError('Please enter a valid password. Your password must be at least 8 characters long and include at least one uppercase letter, one lowercase letter, and one number.');
        return;
        }

        axios.post('api/login', data)
        .then((response) => {
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error)
        })

    }

    return (    
        <div className='auth-form-container'>
            <h2>Login</h2>
            <form className='login-form' onSubmit={handleSubmit}>
                <label htmlFor='email'>email</label> 
                <input value={email} onChange={(e) => setEmail(e.target.value) +setEmailError('') }type='text' placeholder='youremail@gmail.com' id='email' name='email' />
                {errExist == 0 ? '' : <div className='err-div'>{emailError}</div>}
                <label htmlFor='password'>password</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type='password' placeholder='********' id='password' name='password' />
                {errExist == 0 ? '' : <div className='err-div'>{passError}</div>}
                <button type='submit'>Log In</button>
            </form>
            <button className='link-btn' onClick={() => props.onFormSwitch('register')}>Don't have an account? Register here.</button>
        </div>
    )
}
