import React, { useState, useEffect } from 'react';
import axios from 'axios'
export const Login = (props) => {
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passError, setPassError] = useState('');
    const [errExist, setErrExist] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [emails, setEmails] = useState([]);

    var success = {};
    var failed = {
        outline : '1px solid #DE3163'
    };

    // fetch the data gotten from database--------------------------------------------
    useEffect(() => {
        axios.get('http://localhost:3005/api/get')
        .then((response) => {
            setEmails(response.data);
        })
    }, [])
    // ----------------------------------------------------------------------------

    const handleSubmit = (e) => {
        e.preventDefault();

        const emailRegex = /^\S+@\S+\.\S+$/;
        const dbPassword = 'DarknessPrince2002'
        // Email validation
        if (email.trim().length === 0) {
            setEmailError('email is required')
            setErrExist(true)
        }else if (!emailRegex.test(email)) {
            setEmailError('Please enter a valid email address.')
            setErrExist(true)
        }else{
            setErrExist(false)
        }

        // Password validation
        if (pass.trim().length === 0) {
            setPassError('password is required')
            setErrExist(true)
        }else if (emailRegex.test(email) && pass !== dbPassword) {
              setPassError('Password Inccorect')
              setErrExist(true)
            }
            else{
            setErrExist(false)
        }

//-----------------------------------------------------------------------------

        if(errExist === true){
            console.log('there is error')
        }else{
            const data = {
            email : email,
            password : pass
        }
        axios.post("http://localhost:3005/api/insert", data)
        .then((response) => {
            console.log(response.data);
        })
        .catch((error) => {
            console.log(error)
        })
        }

    }
    return (    
        <div className='auth-form-container'>
            <h2>Login</h2>
            <form className='login-form' onSubmit={handleSubmit}>
                <label htmlFor='email'>email</label> 
                <input style={emailError  == '' ? success  : failed}  value={email} onChange={(e) => setEmail(e.target.value) + setEmailError('') }type='text' placeholder='youremail@gmail.com' id='email' name='email' />
                <div>
                    {emailError == '' ? '' : <div className='error-message'>{emailError}</div>}
                </div>
                <label htmlFor='password'>password</label>
                <input style={passError  == '' ? success : failed} value={pass} onChange={(e) => setPass(e.target.value) + setPassError('') } type={showPassword ? 'text' : 'password'}  placeholder='********' id='password' name='password' />
                <div className='show-pass'>
                <div>
                    { passError == '' ? '' : <div className='error-message'>{passError} </div>}
                </div>
                <label> <small>Show Password</small>  </label>
                <input type="checkbox" checked={showPassword} onChange={e => setShowPassword(e.target.checked)} />
                </div>
                <p className='link-btn' onClick={() => props.onFormSwitch('ResetPassword')}>Forgot passwrod ?</p>
                <button type='submit'>Log In</button>
            </form>
            <ul></ul>

        </div>
    )
}
